// Types
interface RecommendationCard {
    id: string;
    category: 'extracurricular' | 'academic' | 'skill' | 'leadership';
    icon: React.ElementType;
    problem: string;
    solution: string;
    actionText: string;
    actionUrl: string;
    color: string;
    bgColor: string;
    borderColor: string;
}

interface CVCheckItem {
    name: string;
    status: 'success' | 'warning' | 'error';
    message: string;
}

interface EssayAnalysis {
    sentiment: string;
    structure: string;
    plagiarism: string;
    suggestion: string;
}

