            bgColor: 'bg-green-50',
            borderColor: 'border-green-200'
        }
    ];

    // Handle CV Upload
    const handleCVUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files && e.target.files[0]) {
