    // Handle CV Upload
    const handleCVUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
        if (e.target.files && e.target.files[0]) {
            const file = e.target.files[0];
            setCvFile(file);

            // Simulate AI analysis
            setTimeout(() => {
                setCvScore(7.5);
